function findDuplicates(arr){
  const seen = new Set();
  const dups = new Set();
  for(const v of arr){ if(seen.has(v)) dups.add(v); else seen.add(v); }
  return Array.from(dups);
}
console.log(findDuplicates([1,4,1,7,4])); // [1,4]

