function sortMarks(arr){
  // simple insertion sort (stable)
  for(let i=1;i<arr.length;i++){
    let key = arr[i];
    let j = i-1;
    while(j>=0 && arr[j]>key){ arr[j+1]=arr[j]; j--; }
    arr[j+1]=key;
  }
  return arr;
}
console.log(sortMarks([88,55,70,99])); // [55,70,88,99]

