function evalPostfix(expr){
  const stack=[];
  for(const c of expr){
    if(!isNaN(c)) stack.push(Number(c));
    else{
      let b=stack.pop(),a=stack.pop();
      if(c==='+') stack.push(a+b);
      else if(c==='-') stack.push(a-b);
      else if(c==='*') stack.push(a*b);
      else stack.push(a/b);
    }
  }
  return stack.pop();
}
console.log(evalPostfix(['5','3','+','2','*'])); // 16

