function nextGreater(arr) {
  const res = [];
  for (let i=0; i<arr.length; i++) {
    let next = -1;
    for (let j=i+1; j<arr.length; j++) {
      if (arr[j] > arr[i]) { next = arr[j]; break; }
    }
    res.push(next);
  }
  return res;
}
console.log(nextGreater([2,1,3])); // [3,3,-1]

